import { validator } from '../../core/index.js';
/**
 * Test given value against to all codecs and returns original input
 * @validator allOf
 */
export function allOf(rules) {
    return validator('allOf', (input, context) => {
        let i;
        let c;
        const l = rules.length;
        for (i = 0; i < l; i++) {
            c = rules[i];
            c(input, context);
        }
        return input;
    });
}
